pragma solidity >=0.4.22 <0.8.0;

// import "./Ownable.sol";
// import "./Escrow.sol";
// import './RefundEscrow.sol';
import '@openzeppelin/contracts/math/SafeMath.sol';

contract Famepay {
    using SafeMath for uint256;

    address payable public influencer;
    address payable public business;
    address payable platform;

    uint256 public campaignId;
    uint256 public incrementalPayment;
    uint256 public jackpotPayment;
    uint256 public deadline;

    bool public jackpotTargetReached;
    bool public incrementalTargetReached;

    bool public businessConfirmed = false;
    bool public influencerConfirmed = false;

    mapping(address => uint256) public depositedFor;
    uint256[] public outstandingIncrementalPayments;

    event BusinessDeposited(address _business, string _message, uint256 _amount);
    event BusinessRefunded(address _business, string _message, uint256 _amount);

    event IncrementInfluencerPayment(address _influencer, string _message, uint256 _amount);
    event JackpotInfluencerPayment(address _influencer, string _message, uint256 _amount);
    event AgreedAmountPayment(address _influencer, string _message, uint256 _amount);

    event InvalidWithdrawal(string _message);

    event CampaignTerminationIncrementPayment(address _influencer, string _message);
    event CampaignTerminationJackpotPayment(address _influencer, string _message);
    event CampaignEnding(string _message);

    modifier onlyInfluencer() {
        require(influencer == msg.sender);
        _;
    }

    modifier onlyBusiness() {
        require(business == msg.sender);
        _;
    }



    constructor(
        address payable _platform,
        address payable _influencer,
        address payable _business,
        uint256 _campaignId,
        uint256 _deadline,
        uint256 _jackpotPayment,
        uint256 _incrementalPayment
    ) public {
        platform = _platform;
        influencer = _influencer;
        business = _business;
        campaignId= _campaignId;
        deadline = _deadline;
        jackpotPayment = _jackpotPayment;
        incrementalPayment = _incrementalPayment;
    }

    // fallback() external payable {
    //     revert();
    // }

    function getTimestamp() public view returns (uint256) {
        return block.timestamp;
    }

    function depositPayment() onlyBusiness public payable {
        require(deadline > block.timestamp, 'The campaign deadline has passed'); //CHANGED!
        depositedFor[influencer] = depositedFor[influencer] + msg.value;
        emit BusinessDeposited(msg.sender, 'has made the required deposit of', msg.value);
    }

    function balance() public view returns (uint256) {
        return depositedFor[influencer];
    }

    function outstandingPayments() public view returns (uint256) {
        return outstandingIncrementalPayments.length;
    }

    function confirmBusinessInfluencer(bool _confirmedBusiness, bool _confirmedInfluencer) public {
        businessConfirmed = _confirmedBusiness;
        influencerConfirmed = _confirmedInfluencer;
    }

    function paymentTargetReached(bool _jackpotTargetReached, bool _incrementalTargetReached) public {
        jackpotTargetReached = _jackpotTargetReached;
        if (_incrementalTargetReached) {
            incrementalTargetReached = _incrementalTargetReached;
            outstandingIncrementalPayments.push(incrementalPayment);
        } else {
            incrementalTargetReached = _incrementalTargetReached;
        }
    }

    function payInfluencer(
        bool _businessConfirmed,
        bool _influencerConfirmed,
        uint256 _confirmedPaymentAmount
    ) public payable {
        require(jackpotTargetReached || incrementalTargetReached || (_businessConfirmed && _influencerConfirmed));
        require(deadline >= block.timestamp);

        //jackpot payment
        if (jackpotTargetReached) {
            require(depositedFor[influencer] >= jackpotPayment);
            depositedFor[influencer] -= jackpotPayment;
            influencer.transfer(jackpotPayment);
            jackpotTargetReached = false;
            emit JackpotInfluencerPayment(influencer, 'has made a jackpot withdrawal', jackpotPayment);
        }

        //incremental payment
        if (incrementalTargetReached) {
            require(depositedFor[influencer] >= incrementalPayment);
            // for multiple outstanding payments
            if (outstandingIncrementalPayments.length >= 1) {
                uint256 outstandingIncrementalPayment = outstandingIncrementalPayments.length.mul(incrementalPayment);
                depositedFor[influencer] -= outstandingIncrementalPayment;
                influencer.transfer(outstandingIncrementalPayment);
                delete outstandingIncrementalPayments;
                incrementalTargetReached = false;
                emit IncrementInfluencerPayment(influencer, 'has made an interval withdrawal', outstandingIncrementalPayment);
            } else {
                depositedFor[influencer] -= incrementalPayment;
                influencer.transfer(incrementalPayment);
                incrementalTargetReached = false;
                emit IncrementInfluencerPayment(influencer, 'has made an interval withdrawal', incrementalPayment);
            }
        }

        //agreed upon sum
        if (_businessConfirmed && _influencerConfirmed) {
            require(_confirmedPaymentAmount < depositedFor[influencer]);
            influencer.transfer(_confirmedPaymentAmount);
            emit AgreedAmountPayment(influencer, 'has made an agreed upon amount withdrawal', _confirmedPaymentAmount);
        }
    }


    function confirmRefund(bool _businessConfirmed, bool _influencerConfirmed) public {
        businessConfirmed = _businessConfirmed;
        influencerConfirmed = _influencerConfirmed;
    }

    function refundPayment() public payable {
        require(deadline <= block.timestamp || (businessConfirmed && influencerConfirmed));
        uint256 payment = depositedFor[influencer];
        depositedFor[influencer] = 0;
        business.transfer(payment);
        emit BusinessRefunded(business, 'was refunded', payment);
    }

    function extendDeadline(uint256 _newDeadline) public {
        require(deadline < _newDeadline);
        deadline = _newDeadline;
    }

    function campaignEnded(uint256 _agreedAmount) public {
        require(deadline <= block.timestamp || (businessConfirmed && influencerConfirmed)); //check multiple require statements
        if (incrementalTargetReached) {
            payInfluencer(businessConfirmed, influencerConfirmed, _agreedAmount);
            emit CampaignTerminationIncrementPayment(business, 'making the remaining incremental payment before campagin termination');
        }
        if (jackpotTargetReached) {
            payInfluencer(businessConfirmed, influencerConfirmed, _agreedAmount);
            emit CampaignTerminationJackpotPayment(business, 'making the remaining jackpot payment before campagin termination');
        }
        emit CampaignEnding('campaign is about to be terminated');
        // selfdestruct;
    }

    function confirmTransaction() public {}


    /*
    function pause() external onlyAdmin {
        super._pause();
    }

    function unpause() external onlyAdmin {
        super._unpause();
    }


    */
}
