// extract logic into private functions and call those with public functions
//eth alarm clock
//x dai
//chainlink
// super fluid for incremental payments
//
pragma solidity ^0.8.4;

import '@openzeppelin/contracts/utils/math/SafeMath.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';

// SPDX-License-Identifier: PRIVATE

contract Famepay is ReentrancyGuard {
    /* LIBRARIES */
    using SafeMath for uint256;

    /* STORAGE */
    address payable public influencer;
    address payable public business;

    uint256 public incrementalPayment;
    uint256 public jackpotPayment;

    uint256 public startDate;
    uint256 public deadline;
    uint256 public simplePostDuration;

    uint256 public campaignId;

    uint256 public incrementalTargetAmount;
    uint256 public jackpotTargetAmount;

    bool public jackpotTargetReached;
    bool public incrementalTargetReached;

    bool public businessConfirmedPay = false;
    bool public influencerConfirmedPay = false;
    uint256 public confirmedPaymentAmount;

    bool public businessConfirmedRefund = false;
    bool public influencerConfirmedRefund = false;

    bool public businessConfirmedExtension = false;
    bool public influencerConfirmedExtension = false;

    bool public campaignIsUnderProtest = false;

    bytes4 public objective;

    uint256 public outstandingIncrementalPayments;
    uint256 public exceededObjectiveAmount;

    mapping(address => uint256) public depositedFor;

    /**Busines actions */
    event BusinessDeposited(address indexed _business, uint256 _amount, uint256 _campaignId);
    event BusinessRefunded(address indexed _business, uint256 _amount, uint256 _campaignId);

    /*Influencer actions */
    event IncrementalTargetReached(uint256 _incrementalTargetsReached, uint256 _campaignId);
    event JackpotTargetReached(bool _targetReached, uint256 _campaignId);

    /*Payment actions */
    event PayInfluencerIncrementalSingle(
        uint256 _allIncrementalPaymentValue,
        uint256 _outstandingIncrementalPayments,
        uint256 _campaignId
    );
    event PayInfluencerIncremental(
        uint256 _singleIncrementalPaymentValue,
        uint256 _outstandingIncrementalPayment,
        uint256 _campaignId
    );
    event JackpotInfluencerPayment(uint256 _amount, uint256 _campaignId);
    event AgreedAmountPayment(address indexed _influencer, uint256 _amount, uint256 _campaignId);

    /*Business/Influencer overrides */
    event ConfirmedPayment(
        bool _businessConfirmed,
        bool _influencerConfirmed,
        uint256 _confirmedPaymentAmount,
        uint256 _campaignId
    );
    event ConfirmedRefund(bool _businessConfirmed, bool _influencerConfirmed, uint256 _campaignId);

    /*Campaign ended actions */
    event CampaignTerminationIncrementPayment(address indexed _influencer, bool _jackpotObjectiveReached, uint256 _campaignId);
    event CampaignTerminationJackpotPayment(address indexed _influencer, bool _jackpotObjectiveReached, uint256 _campaignId);
    event CampaignEnding(uint256 _refundableAmount, bool _jackpotObjectiveReached, uint256 _campaignId);
    event ExtendDeadline(uint256 _newDeadline, uint256 _campaignId);
    event GracePayoutPeriod(uint256 _finalPayoutDeadline, uint256 _campaignId);

    event CheckDeadline(uint256 _deadline);

    modifier onlyInfluencer() {
        require(influencer == msg.sender, 'Only the influencer can call this function');
        _;
    }

    modifier onlyBusiness() {
        require(business == msg.sender, 'Only the business can call this function');
        _;
    }

    modifier onlyAdmin() {
        // require(admin == msg.sender, "Only the admin can call this function");
        _;
    }

    constructor(
        address payable _influencer,
        address payable _business,
        uint256 _startDate,
        uint256 _deadline,
        uint256 _simplePostDuration,
        uint256 _jackpotPayment,
        uint256 _incrementalPayment,
        uint256 _jackpotTargetAmount,
        uint256 _incrementalTargetAmount,
        bytes4 _objective,
        uint256 _campaignId
    ) payable {
        influencer = _influencer;
        business = _business;
        startDate = _startDate;
        deadline = _deadline;
        simplePostDuration = _simplePostDuration;
        jackpotPayment = _jackpotPayment;
        incrementalPayment = _incrementalPayment;
        jackpotTargetAmount = _jackpotTargetAmount;
        incrementalTargetAmount = _incrementalTargetAmount;
        objective = _objective;
        campaignId = _campaignId;
        depositedFor[influencer] = depositedFor[influencer] + address(this).balance;
    }

    function getTimestamp() public view returns (uint256) {
        return block.timestamp;
    }

    /**
     * @dev used for injecting more money for campaigns you want to extend
     */
    function depositPayment() external payable onlyBusiness {
        require(deadline > block.timestamp, 'depositPayment(): The campaign deadline has passed');
        require(msg.value > 0, 'depositPayment(): You must deposit money into the contract');
        jackpotTargetReached = false;
        incrementalTargetReached = false;
        depositedFor[influencer] = depositedFor[influencer] + msg.value;
        emit BusinessDeposited(msg.sender, msg.value, campaignId);
    }

    /**
     * @dev check amount deposited for an influencer
     */
    function depositedForInfluencerBalance() external view returns (uint256) {
        return depositedFor[influencer];
    }

    /**
     * @dev check amount deposited in campaign
     */
    function addressBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev protest a contentious campaign
     * @param _postPosted Protested post must be valid post
     */
    function protestCampaign(bool _postPosted) external onlyBusiness {
        require(deadline < block.timestamp, 'protestCampaign(): payout has already gone through');
        require(_postPosted == true, 'protestCampaign():, protest must be against valid post');
        campaignIsUnderProtest = true;
    }

    /**
     * @dev end campaign protest
     */
    function resolveProtest() external onlyAdmin onlyBusiness {
        require(campaignIsUnderProtest == true, 'cancelProtest(): cannot cancel protest if not under protest');
        require(deadline < block.timestamp, 'cancelProtest(): protest period is over');
        campaignIsUnderProtest = false;
    }

    /**
     * @dev remove 12 hour grace period to allow shorter payouts
     */
    function removeGracePeriod(bool _businessConfirmed, bool _influencerConfirmed)
        external
        onlyAdmin
        onlyBusiness
        onlyInfluencer
    {
        require(
            jackpotTargetReached == true || incrementalTargetReached == true,
            'removeGracePeriod(): only applicable when payout is inbound'
        );
        require(
            _businessConfirmed == true && _influencerConfirmed == true,
            'removeGracePeriod(): both sides must agree to remove grace period'
        );
        deadline -= 43200;
    }

    /**
     * @dev check if objective was achieved
     * @param  _postStat Post stat for incremental payment
     * @param  _postPosted check if post is posted and valid
     */
    //only influencer posts the url(s) throughout the campaign. At the end of the campaign sum of
    //target metric is added and passed to this function to _postStat.

    //influencer must tag the @business on social media which we'll use to validate there eth address
    //pass the successfully valid tagged post to _postPosted

    //business has a protest button which will flag a shady post which will be manually reviewed by team
    //protested posts will delay payout by a day. 12 hour delay period after deadline for business to protest
    //claim your reward button on the frontend so only influencer can call this function

    //TODO investigate forwarding gas cost away from influencer
    //TODO pay now feature: if business & influencer are both happy with present post they can both confirm to pay right now

    function checkCampaignObjectiveReached(
        uint256 _postStat,
        bool _postPosted,
        uint256 _postTimestamp
    ) external {
        require(msg.sender == influencer, 'checkCampaignObjectiveReached(): Caller must be influencer');
        require(_postPosted == true, 'checkCampaignObjectiveReached(): post was not obtained');
        require(deadline <= block.timestamp, 'checkCampaignObjectiveReached(): deadline must be have passed');
        require(_postTimestamp <= deadline, 'checkCampaignObjectiveReached(): invalid post: post must be before the deadline');
        require(_postTimestamp >= startDate, 'checkCampaignObjectiveReached(): post must be after campaign startDate');
        //if it is a simplePost no incremental needed
        if (keccak256(abi.encodePacked(objective)) == keccak256(abi.encodePacked('post'))) {
            require(_postStat >= jackpotTargetAmount, 'checkCampaignObjectiveReached(): jackpot target must be satisfied');
            jackpotTargetReached = true;
            emit JackpotTargetReached(true, campaignId);
            // Extend payment by 12 hours
            deadline = deadline.add(43200);
            emit GracePayoutPeriod(deadline, campaignId);
            //not a simple post
        } else {
            deadline = deadline.add(43200);
            require(
                _postStat >= incrementalTargetAmount,
                'checkCampaignObjectiveReached(): post must at least be greater than incremental to get paid'
            );
            //eligible for jackpot reward and incremental reward
            if (_postStat >= jackpotTargetAmount) {
                require(_postStat >= jackpotTargetAmount, 'checkCampaignObjectiveReached(): jackpot target must be satisfied');
                jackpotTargetReached = true;
                emit JackpotTargetReached(true, campaignId);
                uint256 exceededAmountMin = jackpotTargetAmount.add(incrementalTargetAmount);
                if (_postStat >= exceededAmountMin) {
                    //amount which influencer exceeded jackpot objective
                    exceededObjectiveAmount = _postStat.sub(jackpotTargetAmount);
                    if (exceededObjectiveAmount >= incrementalTargetAmount) {
                        outstandingIncrementalPayments = exceededObjectiveAmount.div(incrementalTargetAmount);
                        incrementalTargetReached = true;
                        //REVIEW the decimal problem
                        emit IncrementalTargetReached(outstandingIncrementalPayments, campaignId);
                        // Extend payment by 12 hours
                        deadline = deadline.add(43200);
                    }
                }
                emit GracePayoutPeriod(deadline, campaignId);
                //only incremental reward
            } else {
                outstandingIncrementalPayments = _postStat.div(incrementalTargetAmount);
                incrementalTargetReached = true;
                //REVIEW the decimal problem
                emit IncrementalTargetReached(outstandingIncrementalPayments, campaignId);
                emit GracePayoutPeriod(deadline, campaignId);
            }
        }
    }

    /**
     * @dev pay the influencer
     * @param _businessConfirmed business has confirmed in case of overriding payment
     * @param _influencerConfirmed influencer has confirmed in case of overriding payment
     */
    function payInfluencer(bool _businessConfirmed, bool _influencerConfirmed) public payable onlyInfluencer {
        require(
            jackpotTargetReached || incrementalTargetReached || (_businessConfirmed && _influencerConfirmed),
            'payInfluencer(): conditions for payment were not met'
        );
        require(campaignIsUnderProtest == false, 'payInfluencer(): Cannot complete payout when under protest');
        require(deadline <= block.timestamp, 'payInfluencer(): this should only trigger after campaign period is over');

        //If attempted msg.value sent when regular address(balance) pay
        if (!_businessConfirmed || !_influencerConfirmed) {
            require(msg.value == 0, 'payInfluencer(), not allowed to forcibly send eth');
        }

        //jackpot payment
        if (jackpotTargetReached) {
            require(depositedFor[influencer] >= jackpotPayment);
            depositedFor[influencer] -= jackpotPayment;
            influencer.transfer(jackpotPayment);
            jackpotTargetReached = false;
            emit JackpotInfluencerPayment(jackpotPayment, campaignId);
        }
        //incremental payment
        if (incrementalTargetReached) {
            require(
                depositedFor[influencer] >= incrementalPayment,
                'payInfluencer(): the attempted payout is more than is despoited in the campaign'
            );
            // for multiple outstanding payments
            if (outstandingIncrementalPayments >= 1) {
                uint256 outstandingIncrementalPayment = outstandingIncrementalPayments.mul(incrementalPayment);
                depositedFor[influencer] -= outstandingIncrementalPayment;
                influencer.transfer(outstandingIncrementalPayment);
                // delete outstandingIncrementalPayments;
                outstandingIncrementalPayments = 0;
                incrementalTargetReached = false;
                emit PayInfluencerIncremental(outstandingIncrementalPayment, outstandingIncrementalPayments, campaignId);
            } else {
                depositedFor[influencer] -= incrementalPayment;
                influencer.transfer(incrementalPayment);
                incrementalTargetReached = false;
                emit PayInfluencerIncrementalSingle(incrementalPayment, outstandingIncrementalPayments, campaignId);
            }
        }
        //agreed upon sum
        if (_businessConfirmed && _influencerConfirmed) {
            require(msg.value >= confirmedPaymentAmount, 'payInfluencer(): only confirmed payment amount or more can be sent');
            influencer.transfer(msg.value);
            emit AgreedAmountPayment(influencer, msg.value, campaignId);
        }
    }

    /**
     * @dev confirm that both parties are in agreement for refund
     * @param _businessConfirmed business confirmation
     * @param _influencerConfirmed influencer confirmation
     */
    function confirmRefund(bool _businessConfirmed, bool _influencerConfirmed) external onlyBusiness onlyInfluencer {
        businessConfirmedRefund = _businessConfirmed;
        influencerConfirmedRefund = _influencerConfirmed;
        emit ConfirmedRefund(_businessConfirmed, _influencerConfirmed, campaignId);
    }

    /**
     * @dev confirm both parties want to pay influencer
     * @param _businessConfirmed business confirmed
     * @param _influencerConfirmed influencer confirmed
     * @param _confirmedPaymentAmount confirmed payment amount
     */
    function confirmPayment(
        bool _businessConfirmed,
        bool _influencerConfirmed,
        uint256 _confirmedPaymentAmount
    ) external onlyBusiness onlyInfluencer {
        businessConfirmedPay = _businessConfirmed;
        influencerConfirmedPay = _influencerConfirmed;
        confirmedPaymentAmount = _confirmedPaymentAmount;
        emit ConfirmedPayment(_businessConfirmed, _influencerConfirmed, _confirmedPaymentAmount, campaignId);
    }

    /**
     * @dev confirm both parties want to extend
     * @param _businessConfirmed business confirmed
     * @param _influencerConfirmed influencer confirmed
     */
    function confirmExtension(bool _businessConfirmed, bool _influencerConfirmed) external onlyBusiness onlyInfluencer {
        businessConfirmedExtension = _businessConfirmed;
        influencerConfirmedExtension = _influencerConfirmed;
    }

    /**
     * @dev extend the campaign deadline
     * @param _newDeadline the new unix timestamp deadline
     */
    function extendDeadline(uint256 _newDeadline) external onlyBusiness onlyInfluencer {
        require(deadline < _newDeadline, 'extendDeadline(): new deadline must be after existing deadline');
        require(
            businessConfirmedExtension && influencerConfirmedExtension,
            'extendDeadline(): both sides must confirm extension'
        );
        deadline = _newDeadline;
        emit ExtendDeadline(_newDeadline, campaignId);
    }

    /**
     * @dev complete campaign and potentially refund business
     */
    function campaignEnded() external payable onlyBusiness onlyInfluencer {
        require(
            deadline >= block.timestamp || (businessConfirmedRefund && influencerConfirmedRefund),
            'campaignEnded(): Either campaign ongoing, or both parties yet to confirm desire to'
        ); //check multiple require statements
        if (jackpotTargetReached) {
            payInfluencer(businessConfirmedPay, influencerConfirmedPay);
            emit CampaignTerminationJackpotPayment(business, true, campaignId);
        } else if (incrementalTargetReached) {
            payInfluencer(businessConfirmedPay, influencerConfirmedPay);
            emit CampaignTerminationIncrementPayment(business, false, campaignId);
        }
        emit CampaignEnding(depositedFor[influencer], false, campaignId);
        selfdestruct(business);
    }

    // function adminCancelCampaign(uint256 _agreedAmount) onlyAdmin internal {
