// SPDX-License-Identifier: PRIVATE

// extract logic into private functions and call those with public functions
//eth alarm clock
//x dai
//chainlink
// super fluid for incremental payments
//
pragma solidity ^0.8.4;

import '@openzeppelin/contracts/utils/math/SafeMath.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';

contract Famepay is ReentrancyGuard {
    /* LIBRARIES */
    using SafeMath for uint256;

    //roles
    AccessControl accessControl;
    bytes32 public constant ADMIN_ROLE = keccak256('ADMIN');

    /* STORAGE */
    address payable public influencer;
    address payable public business;

    uint256 public incrementalPayment;
    uint256 public jackpotPayment;

    uint256 public startDate;
    uint256 public deadline;
    uint256 public simplePostDuration;

    uint256 public campaignId;

    uint256 public incrementalTargetAmount;
    uint256 public jackpotTargetAmount;

    bool public jackpotTargetReached;
    bool public incrementalTargetReached;

    uint256 public businessRevisedPay;
    uint256 public influencerRevisedPay;
    uint256 public revisedPaymentAmount;

    bool public businessConfirmCampaignEnd = false;
    bool public influencerConfirmCampaignEnd = false;

    uint256 public businessProposedDeadline;
    uint256 public influencerProposedDeadline;

    bool public campaignIsUnderProtest = false;

    bytes4 public objective;

    uint256 public outstandingIncrementalPayments;
    uint256 public exceededObjectiveAmount;

    // mapping(address => uint256) public depositedFor;

    /**Busines actions */
    event BusinessDeposited(address indexed _business, uint256 _amount, uint256 _campaignId);

    /*Influencer actions */
    event IncrementalTargetReached(uint256 _incrementalTargetsReached, uint256 _campaignId);
    event JackpotTargetReached(bool _targetReached, uint256 _campaignId);

    /* Grave Payout Period */
    event GracePayoutPeriod(uint256 _availablePayoutDate, uint256 _campaignId);

    /*Payment actions */
    event PayInfluencerIncrementalSingle(uint256 _amount, uint256 _outstandingIncrementalPayment, uint256 _campaignId);
    //unloads all incremntal payments
    event PayInfluencerIncremental(uint256 _amount, uint256 _outstandingIncrementalPayments, uint256 _campaignId);
    event JackpotInfluencerPayment(uint256 _amount, uint256 _campaignId);
    event AgreedAmountPayment(address indexed _influencer, uint256 _amount, uint256 _campaignId);

    /*Payment override */
    event ConfirmedPayment(uint256 _revisedPayment, uint256 _campaignId);
    event ProposedPayment(address indexed _proposer, uint256 _proposedPayment, uint256 _campaignId);

    /* Deadline override*/
    event ExtendDeadline(uint256 _revisedDeadline, uint256 _campaignId);
    event ProposeNewDeadline(address indexed _proposer, uint256 _proposedDeadline, uint256 _campaignId);

    /* Refund override */
    event ConfirmedRefund(address indexed _proposer, uint256 _campaignId);

    /*Campaign ended actions */
    event CampaignTerminationIncrementPayment(
        address indexed _influencer,
        bool _incrementalObjectiveReached,
        uint256 _campaignId
    );
    event CampaignTerminationJackpotPayment(address indexed _influencer, bool _jackpotObjectiveReached, uint256 _campaignId);
    event CampaignEnding(
        uint256 _refundableAmount,
        bool _jackpotObjectiveReached,
        bool _incrementalObjectiveReached,
        uint256 _campaignId
    );

    modifier onlyInfluencer() {
        require(influencer == msg.sender, 'Only the influencer can call this function');
        _;
    }

    modifier onlyBusiness() {
        require(business == msg.sender, 'Only the business can call this function');
        _;
    }

    modifier onlyAdmin() {
        require(accessControl.hasRole(ADMIN_ROLE, msg.sender), 'Only admin can call this function');
        _;
    }

    modifier eitherBusinessOrInfluencer() {
        require(business == msg.sender || influencer == msg.sender, 'Only the business or influencer can call this function');
        _;
    }

    modifier campaignOver() {
        require(block.timestamp >= deadline, 'deadline must have passed');
        _;
    }

    constructor(
        address payable _influencer,
        address payable _business,
        uint256 _startDate,
        uint256 _deadline,
        uint256 _simplePostDuration,
        uint256 _jackpotPayment,
        uint256 _incrementalPayment,
        uint256 _jackpotTargetAmount,
        uint256 _incrementalTargetAmount,
        bytes4 _objective,
        uint256 _campaignId,
        AccessControl _accessControl
    ) payable {
        influencer = _influencer;
        business = _business;
        startDate = _startDate;
        deadline = _deadline;
        simplePostDuration = _simplePostDuration;
        jackpotPayment = _jackpotPayment;
        incrementalPayment = _incrementalPayment;
        jackpotTargetAmount = _jackpotTargetAmount;
        incrementalTargetAmount = _incrementalTargetAmount;
        objective = _objective;
        campaignId = _campaignId;
        accessControl = _accessControl;
    }

    function getTimestamp() public view returns (uint256) {
        return block.timestamp;
    }

    /**
     * @dev used for injecting more money for campaigns you want to extend
     */
    function depositPayment() external payable onlyBusiness {
        require(deadline > block.timestamp, 'depositPayment(): The campaign deadline has passed');
        require(msg.value > 0, 'depositPayment(): You must deposit money into the contract');
        jackpotTargetReached = false;
        incrementalTargetReached = false;
        emit BusinessDeposited(msg.sender, msg.value, campaignId);
    }

    /**
     * @dev check amount deposited in campaign
     */
    function addressBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev protest a contentious campaign
     * @param _postPosted Protested post must be valid post
     */
    function protestCampaign(bool _postPosted) external onlyBusiness campaignOver {
        require(_postPosted == true, 'protestCampaign():, protest must be against valid post');
        campaignIsUnderProtest = true;
    }

    /**
     * @dev end campaign protest (in favour of influencer)
     */
    function resolveProtest() external onlyAdmin {
        require(campaignIsUnderProtest, 'resolveProtest(): cannot cancel protest if not under protest');
        campaignIsUnderProtest = false;
    }

    /**
     * @dev end campaign due to protest (in favour of business)
     */
    function overrideMaliciousCampaign() external onlyAdmin {
        require(campaignIsUnderProtest, 'resolveProtest(): cannot cancel protest if not under protest');
        emit CampaignEnding(address(this).balance, false, false, campaignId);
        selfdestruct(business);
    }

    /**
     * @dev remove 12 hour grace period to allow shorter payouts
     * @param _businessConfirmed business confirms removal of grace period
     * @param _influencerConfirmed influencer confirms removal of grace period
     */
    function removeGracePeriod(bool _businessConfirmed, bool _influencerConfirmed) external eitherBusinessOrInfluencer {
        require(jackpotTargetReached || incrementalTargetReached, 'removeGracePeriod(): only applicable when payout is inbound');
        require(_businessConfirmed && _influencerConfirmed, 'removeGracePeriod(): both sides must agree to remove grace period');
        deadline -= 43200;
    }

    uint256 public actualPostDuration;

    /**
     * @dev check if objective was achieved
     * @param _postStat Post stat for incremental payment
     * @param _postPosted Check if post is posted and valid
     * @param _postTimestamp Timestamp of post
     */

    //TODO investigate forwarding gas cost away from influencer
    //TODO pay now feature: if business & influencer are both happy with present post they can both confirm to pay right now

    function checkCampaignObjectiveReached(
        uint256 _postStat,
        bool _postPosted,
        uint256 _postTimestamp
    ) external campaignOver {
        require(msg.sender == influencer, 'checkCampaignObjectiveReached(): Caller must be influencer');
        require(_postPosted, 'checkCampaignObjectiveReached(): post was not obtained');
        require(deadline >= _postTimestamp, 'checkCampaignObjectiveReached(): invalid post: post must be before the deadline');
        require(_postTimestamp >= startDate, 'checkCampaignObjectiveReached(): post must be after campaign startDate');

        //if it is a simplePost no incremental needed
        if (keccak256(abi.encodePacked(objective)) == keccak256(abi.encodePacked('post'))) {
            require(_postStat >= jackpotTargetAmount, 'checkCampaignObjectiveReached(): jackpot target must be satisfied');

            //time post was up
            actualPostDuration = block.timestamp - _postTimestamp;

            require(
                actualPostDuration >= simplePostDuration,
                'checkCampaignObjectiveReached(): post duration must meet required duration'
            );

            //jackpot achieved
            jackpotTargetReached = true;

            emit JackpotTargetReached(true, campaignId);

            // Extend payment by 12 hours
            deadline = deadline + 43200;

            emit GracePayoutPeriod(deadline, campaignId);
        } else {
            require(
                _postStat >= incrementalTargetAmount,
                'checkCampaignObjectiveReached(): post must at least be greater than incremental to get paid'
            );
            //eligible for jackpot reward
            if (_postStat >= jackpotTargetAmount) {
                require(_postStat >= jackpotTargetAmount, 'checkCampaignObjectiveReached(): jackpot target must be satisfied');

                //jackpot achieved
                jackpotTargetReached = true;

                emit JackpotTargetReached(true, campaignId);

                //extra incremental objective achievement
                uint256 exceededAmountMin = jackpotTargetAmount + incrementalTargetAmount;

                //Also eligible for bonus incremental reward
                if (_postStat >= exceededAmountMin) {
                    //amount which influencer exceeded jackpot objective
                    exceededObjectiveAmount = _postStat - jackpotTargetAmount;

                    if (exceededObjectiveAmount >= incrementalTargetAmount) {
                        //incremental payments owed
                        outstandingIncrementalPayments = exceededObjectiveAmount / incrementalTargetAmount;

                        //incremental achieved
                        incrementalTargetReached = true;

                        //REVIEW the decimal problem
                        emit IncrementalTargetReached(outstandingIncrementalPayments, campaignId);

                        // Extend payment by 12 hours
                        deadline = deadline + 43200;
                    }
                }

                //add grace period
                deadline = deadline + 43200;

                emit GracePayoutPeriod(deadline, campaignId);
            } else {
                //incremental payments owed
                outstandingIncrementalPayments = _postStat / incrementalTargetAmount;

                //incremental achieved
                incrementalTargetReached = true;

                //REVIEW the decimal problem
                emit IncrementalTargetReached(outstandingIncrementalPayments, campaignId);
                deadline = deadline + 43200;

                emit GracePayoutPeriod(deadline, campaignId);
            }
        }
    }

    /**
     * @dev pay the influencer
     */
    function payInfluencer() public payable eitherBusinessOrInfluencer campaignOver {
        require(
            jackpotTargetReached || incrementalTargetReached || revisedPaymentAmount > 0,
            'payInfluencer(): conditions for payment were not met'
        );
        require(!campaignIsUnderProtest, 'payInfluencer(): Cannot complete payout when under protest');
        //jackpot payment
        if (jackpotTargetReached) {
            // require(depositedFor[influencer] >= jackpotPayment);
            require(address(this).balance >= jackpotPayment);

            influencer.transfer(jackpotPayment);
            jackpotTargetReached = false;
            emit JackpotInfluencerPayment(jackpotPayment, campaignId);
        }
        //incremental payment
        if (incrementalTargetReached) {
            require(
                address(this).balance >= incrementalPayment,
                'payInfluencer(): the attempted payout is more than is deposited in the campaign'
            );
            // for multiple outstanding payments
            if (outstandingIncrementalPayments >= 1) {
                uint256 outstandingIncrementalPayment = outstandingIncrementalPayments.mul(incrementalPayment);

                influencer.transfer(outstandingIncrementalPayment);

                outstandingIncrementalPayments = 0;
                incrementalTargetReached = false;
                emit PayInfluencerIncremental(outstandingIncrementalPayment, outstandingIncrementalPayments, campaignId);
            } else {
                influencer.transfer(incrementalPayment);
                incrementalTargetReached = false;
                emit PayInfluencerIncrementalSingle(incrementalPayment, outstandingIncrementalPayments, campaignId);
            }
        }
        //agreed upon sum
        if (revisedPaymentAmount > 0) {
            // require(msg.value == revisedPaymentAmount, 'payInfluencer(): only confirmed payment amount or more can be sent');
            influencer.transfer(revisedPaymentAmount);
            emit AgreedAmountPayment(influencer, msg.value, campaignId);
        }
    }

    /**
     * @dev business confirmed refund
     * @param _confirmed business confirmation
     **/
    function businessConfirmEnd(bool _confirmed) external onlyBusiness {
        businessConfirmCampaignEnd = _confirmed;
        emit ConfirmedRefund(msg.sender, campaignId);
    }

    /**
     * @dev influencer confirmed refund
     * @param _confirmed business confirmation
     **/
    function influencerConfirmEnd(bool _confirmed) external onlyInfluencer {
        influencerConfirmCampaignEnd = _confirmed;
        emit ConfirmedRefund(msg.sender, campaignId);
    }

    /**
     * @dev business confirmed specific payout
     * @param _revisedPayment revised payment amount
     */
    function businessConfirmPayment(uint256 _revisedPayment) external onlyBusiness {
        require(_revisedPayment < address(this).balance, 'businessConfirmPayment(): amount must be lower than deposited');
        if (influencerRevisedPay == _revisedPayment) {
            revisedPaymentAmount = _revisedPayment;
            emit ConfirmedPayment(_revisedPayment, campaignId);
        } else {
            businessRevisedPay = _revisedPayment;
            emit ProposedPayment(msg.sender, _revisedPayment, campaignId);
        }
    }

    /**
     * @dev influencer confirmed specific payout
     * @param _revisedPayment revised payment amount
     */
    function influencerConfirmPayment(uint256 _revisedPayment) external onlyInfluencer {
        require(_revisedPayment < address(this).balance, 'businessConfirmPayment(): amount must be lower than deposited');
        if (businessRevisedPay == _revisedPayment) {
            revisedPaymentAmount = _revisedPayment;
            emit ConfirmedPayment(_revisedPayment, campaignId);
        } else {
            influencerRevisedPay = _revisedPayment;
            emit ProposedPayment(msg.sender, _revisedPayment, campaignId);
        }
    }

    /**
     * @dev business confirmed new deadline
     * @param _newDeadline revised deadline
     */
    function businessConfirmExtension(uint256 _newDeadline) external onlyBusiness {
        require(block.timestamp <= _newDeadline, 'extendDeadline(): new deadline must be after existing deadline');
        if (influencerProposedDeadline == _newDeadline) {
            deadline = _newDeadline;
            emit ExtendDeadline(_newDeadline, campaignId);
        } else {
            businessProposedDeadline = _newDeadline;
            emit ProposeNewDeadline(msg.sender, _newDeadline, campaignId);
        }
    }

    /**
     * @dev influencer confirmed new deadline
     * @param _newDeadline revised deadline
     */
    function influencerConfirmExtension(uint256 _newDeadline) external onlyInfluencer {
        require(block.timestamp <= _newDeadline, 'extendDeadline(): new deadline must be after existing deadline');
        if (businessProposedDeadline == _newDeadline) {
            deadline = _newDeadline;
            emit ExtendDeadline(_newDeadline, campaignId);
        } else {
            influencerProposedDeadline = _newDeadline;
            emit ProposeNewDeadline(msg.sender, _newDeadline, campaignId);
        }
    }

    /**
     * @dev complete campaign and potentially refund business
     */
    function campaignEnded() external eitherBusinessOrInfluencer {
        require(
            block.timestamp >= deadline || (businessConfirmCampaignEnd && influencerConfirmCampaignEnd),
            'campaignEnded(): Either campaign ongoing, or both parties yet to confirm desire to'
        );
        if (jackpotTargetReached) {
            payInfluencer();
            emit CampaignTerminationJackpotPayment(business, true, campaignId);
        } else if (incrementalTargetReached) {
            payInfluencer();
            emit CampaignTerminationIncrementPayment(business, false, campaignId);
        }
        emit CampaignEnding(address(this).balance, false, false, campaignId);
        selfdestruct(business);
    }

    /*
    function pause() external onlyAdmin {
        super._pause();
    }

    function unpause() external onlyAdmin {
        super._unpause();
    }
    */
}

