// SPDX-License-Identifier: PRIVATE

// extract logic into private functions and call those with public functions
//eth alarm clock
//x dai
//chainlink
// super fluid for incremental payments

pragma solidity ^0.8.4;

import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/security/Pausable.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';

contract FamepayBounty is ReentrancyGuard, Pausable {
    /* ENUM */
    enum BountyType {
        variablePot,
        fixedPot
    }

    /* STORAGE */

    //roles
    AccessControl accessControl;
    bytes32 public constant ADMIN_ROLE = keccak256('ADMIN');

    struct Influencer {
        address payable influencer;
        bool paidOut;
        bool isWinner;
        bool isUnderProtest;
        uint256 gracePayout;
    }

    BountyType public bountyType;

    address payable public business;

    uint256 public totalBountyPot;
    uint256 public maxJackpotReward;
    uint256 public fixedPotReward;

    uint256 public startDate;
    uint256 public deadline;
    uint256 public simplePostDuration;

    uint256 public campaignId;

    //objective target
    uint256 public jackpotTargetAmount;

    uint256 public confirmedPaymentAmount;

    bytes4 public objective;

    uint256 public maxWinners; //max amount who can win
    uint256 public totalWinners; //number of winners that won

    //all participating influencers
    mapping(address => Influencer) public influencers;

    //all removed influencers
    mapping(address => address) public blacklistedInfluencers;

    //used for paying out all winners when campaign ends
    address payable[] public winningInfluencers;
    address public winningInfluencer;

    bool public campaignIsUnderProtest = false;
    address[] public influencersUnderProtest;
    /////////* EVENTS *///////

    /* Influencer Actions*/
    event NewInfluencerAdded(address indexed _newInfluencer, uint256 _bountyId);
    event InfluencerRemoved(address indexed _influencer, uint256 _bountyId);
    event InfluencerBlacklisted(address indexed _influencer, uint256 _bountyId);

    /*Target Status*/
    event JackpotTargetReached(address indexed _winningInfluencer, uint256 _bountyId);

    /*Payment Actions */
    event JackpotInfluencerPayment(uint256 _amount, address indexed _influencer, uint256 _bountyId);
    // event JackpotInfluencerPaymentAll(uint256 _amount, address payable[] _influencers, uint256 _bountyId);

    /*Campaign Ended Actions */
    event CampaignEnding(uint256 _refundableAmount, uint256 _bountyId);

    /////////* MODIFIERS*///////

    modifier onlyBusiness() {
        require(business == msg.sender, 'Only the business can call this function');
        _;
    }

    modifier businessOrWinningInfluencer() {
        if (msg.sender == influencers[msg.sender].influencer) {
            require(influencers[msg.sender].isWinner, 'Only a winning influencer can call this function');
        } else {
            require(business == msg.sender, 'Only the business or influencer can call this function');
        }
        _;
    }

    modifier onlyWinningInfluencer() {
        // if (msg.sender == influencers[msg.sender].influencer) {
        require(influencers[msg.sender].isWinner, 'Only a winning influencer can call this function');
        // }
        _;
    }

    modifier onlyAdmin() {
        require(accessControl.hasRole(ADMIN_ROLE, msg.sender), 'Only admin can call this function');
        _;
    }

    modifier adminOrBusiness() {
        require(msg.sender == business || accessControl.hasRole(ADMIN_ROLE, msg.sender), 'Caller must be business or admin');
        _;
    }

    modifier campaignOver() {
        require(block.timestamp >= deadline, 'deadline must have passed');
        _;
    }

    /////////* CONSTRUCTOR *///////
    constructor(
        address payable _business,
        uint256 _startDate,
        uint256 _deadline,
        uint256 _simplePostDuration,
        uint256 _maxJackpotReward,
        uint256 _jackpotTargetAmount, //payout per winner
        // uint256 _fixedPotReward, //payout per influencer
        uint256 _bountyId,
        uint256 _maxWinners,
        AccessControl _accessControl,
        bytes4 _objective,
        bytes32 _bountyType
    ) payable {
        business = _business;
        startDate = _startDate;
        deadline = _deadline;
        simplePostDuration = _simplePostDuration;
        maxJackpotReward = _maxJackpotReward;
        jackpotTargetAmount = _jackpotTargetAmount;
        maxWinners = _maxWinners;
        accessControl = _accessControl;
        campaignId = _bountyId;
        objective = _objective;

        bytes32 variablePot = 'varPot';
        bytes32 fixedPot = 'fixPot';

        if (_bountyType == variablePot) {
            bountyType = BountyType.variablePot;
        }
        if (_bountyType == fixedPot) {
            bountyType = BountyType.fixedPot;
            fixedPotReward = _maxJackpotReward;
        }
        totalBountyPot = address(this).balance;
    }

    /////////* FUNCTIONS *///////

    /**
     * @dev check amount deposited in campaign
     */
    function addressBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev used for adding new influencer for bounty program
     */
    function addInfluencer() external {
        require(msg.sender != business, 'addInfluencer(): business cannot be an influencer');
        require(deadline >= block.timestamp, 'addInfluencer(): deadline has passed');
        require(influencers[msg.sender].influencer == address(0), 'addInfluencer(): influencer already added');
        influencers[msg.sender] = Influencer(payable(msg.sender), false, false, false, 0);
        emit NewInfluencerAdded(msg.sender, campaignId);
    }

    /**
     * @dev remove influencer from campaign
     * @param _influencer influencer to be moreved
     */
    function removeInfluencer(address _influencer) external onlyBusiness {
        influencers[_influencer] = Influencer(payable(address(0)), false, false, false, 0);
        emit InfluencerRemoved(_influencer, campaignId);
    }

    /**
     * @dev check if objective was achieved
     * @param _postStat post stat for incremental payment
     * @param _postPosted check if post is posted and valid
     * @param _postTimestamp post date
     */
    //TODO investigate forwarding gas cost away from influencer
    function checkCampaignObjectiveReached(
        uint256 _postStat,
        bool _postPosted,
        uint256 _postTimestamp
    ) external nonReentrant campaignOver {
        require(
            msg.sender == influencers[msg.sender].influencer,
            'checkCampaignObjectiveReached(): Must be registered influencer'
        );
        require(_postPosted, 'checkCampaignObjectiveReached(): post was not obtained');
        require(_postTimestamp <= deadline, 'checkCampaignObjectiveReached(): post must be before the deadline');
        require(_postTimestamp >= startDate, 'checkCampaignObjectiveReached(): post must be after campaign startDate');
        require(_postStat >= jackpotTargetAmount, 'checkCampaignObjectiveReached(): jackpot target must be satisfied');
        require(!influencers[msg.sender].isWinner, 'checkCampaignObjectiveReached(): winner already registered');
        winningInfluencers.push(payable(msg.sender));
        require(maxWinners >= winningInfluencers.length, 'checkCampaignObjectiveReached(): too many winners claimed');
        influencers[msg.sender].isWinner = true;
        totalWinners = winningInfluencers.length;

        influencers[msg.sender].gracePayout = deadline + 43200;
        emit JackpotTargetReached(msg.sender, campaignId);
    }

    function getWinnersLength() external view returns (uint256) {
        return winningInfluencers.length;
    }

    /**
     * @dev pay the influencer
     */
    function payInfluencer() public nonReentrant onlyWinningInfluencer campaignOver {
        require(!influencers[msg.sender].paidOut, 'payInfluencer(): influencer already paid out');
        require(!influencers[msg.sender].isUnderProtest, 'payInfluencer(): cannot complete payout when under protest');
        require(
            influencers[msg.sender].gracePayout <= block.timestamp,
            'payInfluencer(): influencer cannot be paid until grace period is over'
        );
        uint256 paymentPerInfluencerPot = bountyType == BountyType.variablePot ? totalBountyPot / totalWinners : fixedPotReward;
        //limit to max jackpot
        if (paymentPerInfluencerPot > maxJackpotReward && bountyType == BountyType.variablePot) {
            paymentPerInfluencerPot = maxJackpotReward;
        }

        payable(msg.sender).transfer(paymentPerInfluencerPot);
        influencers[msg.sender].paidOut = true;
        emit JackpotInfluencerPayment(paymentPerInfluencerPot, msg.sender, campaignId);

        //remove influencer that claims $
        /*
        for (uint256 i = 0; i < winningInfluencers.length - 1; i++) {
            winningInfluencers[i] = winningInfluencers[i + 1];
        }
        winningInfluencers.pop();
        campaign ending payout
        if (msg.sender == business) {
            for (uint256 i = 0; i < winningInfluencers.length; i++) {
                winningInfluencers[i].transfer(paymentPerInfluencerPot);
                influencers[winningInfluencers[i]].paidOut = true;
            }
            emit JackpotInfluencerPaymentAll(paymentPerInfluencerPot, winningInfluencers, campaignId);
        }
        */
    }

    /**
     * @dev protest a contentious campaign
     * @param _influencer influencer business is protesting
     */
    function protestWinningInfluencer(address _influencer) external onlyBusiness {
        require(!influencers[_influencer].isUnderProtest, 'protestWinningInfluencer(): influencer is already under protest');
        influencers[_influencer].isUnderProtest = true;
        campaignIsUnderProtest = true;
    }

    /**
     * @dev end campaign protest (in favour of influencer)
     * @param _influencer influencer business is protesting
     */
    function resolveProtest(address _influencer) external adminOrBusiness {
        require(campaignIsUnderProtest, 'resolveProtest(): cannot cancel protest if not under protest');
        require(influencers[_influencer].isUnderProtest, 'protestWinningInfluencer(): influencer is already under protest');
        influencers[_influencer].isUnderProtest = false;
        if (influencersUnderProtest.length < 1) campaignIsUnderProtest = false;
    }

    /**
     * @dev blacklist malicious influencer
     * @param _influencer influencer to be removed
     */
    function blacklistInfluencer(address _influencer) external onlyAdmin {
        require(blacklistedInfluencers[_influencer] == address(0), 'blacklistInfluencer(): influencer already blacklisted');
        require(influencers[_influencer].isUnderProtest, 'blacklistInfluencer(): influencer not under protest');
        influencers[_influencer] = Influencer(payable(address(0)), false, false, false, 0);
        blacklistedInfluencers[_influencer] = _influencer;
        if (influencersUnderProtest.length < 1) campaignIsUnderProtest = false;
        emit InfluencerBlacklisted(_influencer, campaignId);
    }

    function getTimestamp() external view returns (uint256) {
        return block.timestamp;
    }

    /**
     * @dev complete campaign and potentially refund business
     */
    function campaignEnded() public onlyBusiness campaignOver {
        require(!campaignIsUnderProtest, 'campaignEnded(): cannot end campaign that is under protest');
        uint256 graceCashoutPeriod = 88400;
        //added extra require statement for clearer revert message
        if (winningInfluencers.length > 0) {
            require(block.timestamp >= deadline + graceCashoutPeriod, 'campaignEnded(): grace cashout period ongoing');
        }
        emit CampaignEnding(address(this).balance, campaignId);
        selfdestruct(business);
    }

    function pause() external onlyAdmin {
        super._pause();
    }

    function unpause() external onlyAdmin {
        super._unpause();
    }

    fallback() external payable onlyBusiness campaignOver {}
}

