pragma solidity >=0.4.22 <0.8.0;
import './Famepay.sol';

contract FamepayFactory {
    // Famepay[] private famepayCampaigns;
    address private admin;
    address payable private platform;
    mapping(uint256 => address) private famepayCampaigns;
    // mapping(uint256 => Famepay) private famepayCampaigns;
    event NewFamepayCampaignCreated(
        uint256 _date,
        address _campaignAddress,
        address _influencer,
        address _business,
        uint256 _campaignId,
        uint256 _deadline,
        uint256 _jackpotPayment,
        uint256 _incrementalPayment
    );

    constructor() public {
        admin = msg.sender;
    }

    function newFamepayCampaign(
        address payable _platform,
        address payable _influencer,
        address payable _business,
        uint256 _campaignId,
        uint256 _deadline,
        uint256 _jackpotPayment,
        uint256 _incrementalPayment
    ) public {
        Famepay famepay = new Famepay(_platform, _influencer, _business, _campaignId, _deadline, _jackpotPayment, _incrementalPayment);
        famepayCampaigns[_campaignId] = address(famepay);
        emit NewFamepayCampaignCreated(block.timestamp, address(famepay), _influencer, _business, _campaignId, _deadline, _jackpotPayment, _incrementalPayment);
    }

    function getCampaign(uint256 _campaignId) public view returns (address) {
        return famepayCampaigns[_campaignId];
    }
}
